mapper.insert:
INSERT INTO ITEM (
    ITEM_CATEGORY
    , ITEM_NAME
    , ITEM_PRICE
    , ITEM_STATUS
    , ITEM_INTRO
) VALUES (
    #{itemCategory}
    , #{itemName}
    , #{itemPrice}
    , #{itemStatus}
    , #{itemIntro}
)

mapper.update:
UPDATE ITEM
SET
    ITEM_CATEGORY = #{itemCategory}
    , ITEM_NAME = #{itemName}
    , ITEM_PRICE = #{itemPrice}
    , ITEM_STATUS = #{itemStatus}
    , ITEM_INTRO = #{itemIntro}
WHERE ITEM_NUM = #{itemNum}
