mapper.insert:
INSERT INTO MEM_BUY (
    CLOTHING_NUM
    , MEM_ID
    , BUY_CNT
    , ORDER_NUM
) VALUES (
    #{clothingNum}
    , #{memId}
    , #{buyCnt}
    , (SELECT IFNULL(MAX(ORDER_NUM), 0) + 1
        FROM MEM_BUY)
)

mapper.select:
SELECT BUY_NUM , ORDER_NUM , IF(COUNT(ORDER_NUM) = 1 , MAX((SELECT CLOTHING_NAME FROM CLOTHING WHERE CLOTHING_NUM = B.CLOTHING_NUM)) , CONCAT(MAX((SELECT CLOTHING_NAME FROM CLOTHING WHERE CLOTHING_NUM = B.CLOTHING_NUM)) , ' 외 ', COUNT(ORDER_NUM) - 1, '건')) CLOTHING_NAME , MAX((SELECT MEM_ID FROM CLOTHING WHERE CLOTHING_NUM = B.CLOTHING_NUM)) MEM_ID , SUM((SELECT PRICE FROM CLOTHING WHERE CLOTHING_NUM = B.CLOTHING_NUM) * BUY_CNT) TOTAL_PRICE , MAX(BUY_DATE) BUY_DATE FROM MEM_BUY B WHERE 1 = 1
AND ORDER_NUM = #{orderNum}
AND MEM_ID LIKE CONCAT('%', #{memId}, '%')
AND BUY_DATE >= #{fromDate}
AND BUY_DATE < ADDDATE(#{toDate}, 1)
GROUP BY ORDER_NUM ORDER BY BUY_DATE DESC
